CREATE TABLE "knowledge" ("ID" INTEGER PRIMARY KEY  NOT NULL  UNIQUE , "title" VARCHAR NOT NULL , "description" TEXT, "author" VARCHAR NOT NULL , "created" DATETIME);
CREATE TABLE "knowledge_dependencies" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "knowledge_ID" INTEGER NOT NULL , "needs_ID" INTEGER NOT NULL );
CREATE TABLE "knowledge_originators" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "knowledge_ID" INTEGER NOT NULL , "originator_ID" INTEGER NOT NULL );
CREATE TABLE "knowledge_references" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "knowledge_ID" INTEGER NOT NULL , "referenced_ID" INTEGER NOT NULL );
CREATE TABLE "knowledge_sources" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "knowledge_ID" INTEGER NOT NULL , "source_ID" INTEGER NOT NULL );
CREATE TABLE "knowledge_tags" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "knowledge_ID" INTEGER NOT NULL , "tag_ID" INTEGER NOT NULL );
CREATE TABLE "persons" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "pre_name" VARCHAR NOT NULL , "sur_name" VARCHAR NOT NULL );
CREATE TABLE "publications" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "author_ID" INTEGER, "title" VARCHAR NOT NULL , "description" TEXT);
CREATE TABLE "tag_parents" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "tagID" INTEGER NOT NULL , "parentTagID" INTEGER NOT NULL );
CREATE TABLE "tags" ("ID" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "name" VARCHAR NOT NULL );
